mapper.insert:
insert into Message (id, senderid, receiverid, 
  subject, title, status, 
  date, attachment, tag, 
  labels, body
  )
values (#{id,jdbcType=INTEGER}, #{senderid,jdbcType=VARCHAR}, #{receiverid,jdbcType=VARCHAR}, 
  #{subject,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, 
  #{date,jdbcType=VARCHAR}, #{attachment,jdbcType=VARCHAR}, #{tag,jdbcType=VARCHAR}, 
  #{labels,jdbcType=VARCHAR}, #{body,jdbcType=LONGVARCHAR}
  )

mapper.select:
select count(*) as count from MessageView 1 = 1
and receiverid = #{receiverid,jdbcType=VARCHAR}
and sendid = #{senderid,jdbcType=VARCHAR}
and labels like '%' #{label,jdbcType=VARCHAR} '%'
and (email like '%' #{search,jdbcType=VARCHAR} '%' or subject like '%' #{search,jdbcType=VARCHAR} '%' or status like '%' #{search,jdbcType=VARCHAR} '%' or sender like '%' #{search,jdbcType=VARCHAR} '%' or title like '%' #{search,jdbcType=VARCHAR} '%' or date like '%' #{search,jdbcType=VARCHAR} '%')

select * from MessageView    1 = 1

  and receiverid = #{receiverid,jdbcType=VARCHAR}


  and sendid = #{senderid,jdbcType=VARCHAR}


  and labels like '%' #{label,jdbcType=VARCHAR} '%'


  and (email like '%' #{search,jdbcType=VARCHAR} '%' or
  subject like '%' #{search,jdbcType=VARCHAR} '%' or
  status like  '%' #{search,jdbcType=VARCHAR} '%' or
  sender like  '%' #{search,jdbcType=VARCHAR} '%' or
  title like  '%' #{search,jdbcType=VARCHAR} '%' or
  date like  '%' #{search,jdbcType=VARCHAR} '%')


order by ${orderColumn} ${order}
limit #{currentIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
